$(document).ready(function () {
    var trigger = $('#link a'),
      container = $('#page-content');

    trigger.on('click', function () {
      var $this = $(this),
        target = $this.data('target');
  
    container.load(target);
    return false;
  });
});

$(document).ready(function () {
  var trigger = $('#faculty button'),
    container = $('#study');

  trigger.on('click', function () {
    var $this = $(this),
      target = $this.data('target');

    $('#course').load('empty.php');
    container.load(target);
    return false;
  });
});

function years(value) {
  $('#course').load(value);
};


